sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {

	var rightnow = new GlideDateTime();

	var overDue = new GlideRecord('x_58872_needit_needit_task');
	overDue.addQuery('due_date', '<=', rightnow);
	overDue.addQuery('state', '!=', 3);
	overDue.addQuery('assigned_to', current.assigned_to);

	overDue.query();

	if(overDue.getRowCount() > 1){
	template.print('In addition to this overdue NeedIt task, you have ' + (overDue.getRowCount() - 1) + " more overdue NeedIt Tasks.");
	}

})(current, template, email, email_action, event);
